import styled from "styled-components";

const Button = styled.button`
    width: 100%;
    height: 114px;
    display: flex;
    flex-direction: column;
    align-items: left;
    justify-content: space-between;
    border: none;
    border-radius: 4.5px;

    font-size: 20px;
    line-height: 26px;
    text-align: center;

    background: #A328D6;
    color: #FFFFFF;

    div{
        padding: 5px;
    };

`;

export default Button;
